class AverageEmbeddingsAnnotator(override val uid: String)
  extends AnnotatorModel[AverageEmbeddingsAnnotator]
    with HasSimpleAnnotate[AverageEmbeddingsAnnotator]
    with HasInputAnnotationCols {

  def this() = this(Identifiable.randomUID("AVERAGE_EMBEDDINGS_ANNOTATOR"))




  // Implement the inputAnnotatorTypes member from the HasInputAnnotationCols trait
  override val outputAnnotatorType: AnnotatorType = SENTENCE_EMBEDDINGS

  /**
   * Input annotator type : SENTENCE_EMBEDDINGS
   */
  override val inputAnnotatorTypes: Array[String] = Array(AnnotatorType.SENTENCE_EMBEDDINGS)

  /**
   * Names of input annotation cols containing embeddings
   */
  override def setInputCols(value: Array[String]): this.type = set(inputCols, value)



   def setInputCol(value: Array[String]): this.type = set(inputCols, value)

  /**
   * Takes average of  two sentence embeddings
   */

  override def annotate(annotations: Seq[Annotation]): Seq[Annotation] = {
    val filteredAnnotations = annotations.filter(_.annotatorType == AnnotatorType.SENTENCE_EMBEDDINGS)
    (0 until filteredAnnotations.size / 2).map { index =>
      val filtered = filteredAnnotations.filter(annotation => annotation.metadata("sentence").toInt == index)
      val mergedEmbeddings = avgEmbeddings(filtered.head.embeddings, filtered.last.embeddings)

      Annotation(
        annotatorType = outputAnnotatorType,
        begin = filtered.head.begin,
        end = filtered.head.end,
        result = filtered.head.result,
        metadata = filtered.head.metadata,
        embeddings = mergedEmbeddings.toArray

      )
    }


  }


  /**
   * Helper method for taking average of two sentence embeddings
   */
  def avgEmbeddings(embeddings1: Array[Float], embeddings2: Array[Float]): Array[Float] = {
    val sizeDiff = math.abs(embeddings1.length - embeddings2.length)
    val smallerEmbeddings = if (embeddings1.length < embeddings2.length) embeddings1 else embeddings2
    val largerEmbeddings = if (embeddings1.length < embeddings2.length) embeddings2 else embeddings1
    val resizedEmbeddings1 = smallerEmbeddings ++ largerEmbeddings.takeRight(sizeDiff)
    val resizedEmbeddings2 = largerEmbeddings
    val avgEmbeddings = (resizedEmbeddings1, resizedEmbeddings2).zipped.map { (value1, value2) =>
      (value1 + value2)/2
    }
    avgEmbeddings
  }



}


